import{_ as a,r as n,o,c as i,a as e,b as t,d as l,e as h}from"./app-BFWdJpBm.js";const c={},d={id:"全面建设社会主义的开端",tabindex:"-1"},r={class:"header-anchor",href:"#全面建设社会主义的开端"},p=e("p",null,"社会主义改造基本结束，社会主义经济制度和政治制度已 经建立，马克思主义在意识形态领域占据了主导地位。这标志着我国完成了从新民主主义向社会主义的转变，进入了社会主义的初级阶段，开始了全面建设社会主义的新时期。",-1),_=e("p",null,"当时中国主要面临两大问题：一个是如何处理好社会主义条件下的阶级斗争问题；另一个是如何处理好社会主义建设中的规模和速度问题",-1),u=e("p",null,[t("毛泽东"),e("em",null,"提出"),t("的关于实行马克思主义同中国实际的“第二次结合”的任务，为探索适合中国情况的社会主义建设道路，提供了基本的指导原则。")],-1),m={id:"中共八大路线的制定",tabindex:"-1"},f={class:"header-anchor",href:"#中共八大路线的制定"},x=e("p",null,"国内主要矛盾已经不再是工人阶级和资产阶级的矛盾，而是人民对于经济文化迅速发展的需要同当前经济文化不能满足人民需要的状况之间的矛盾",-1),g=e("p",null,"根本任务已经是在新的生产关系下保护和发展生产力",-1),b=e("p",null,"陈云提出“三个主体、三个补充”的思想，即国家经营和集体经营是主体，一定数量的个体经营为补充；计划生产是主体，一定范围的自由生产为补充；国家市场是主体,一定范围的自由市场为补充。",-1),v=e("p",null,"毛泽东提出，可以消灭了资本主义，又搞资本主义，并把这称作“新经济政策”。",-1),k={id:"探索社会主义建设道路的初步成果",tabindex:"-1"},y={class:"header-anchor",href:"#探索社会主义建设道路的初步成果"},N=h('<h3 id="《论十大关系》" tabindex="-1"><a class="header-anchor" href="#《论十大关系》"><span>《论十大关系》</span></a></h3><p>报告概括提出了十大关系。这十大关系围绕一个基本方针，，即调动国内外一切积极因素，为社会主义服务。</p><p>《论十大关系》是以毛泽东为主要代表的中国共产党人开始探索中国自己的社会主义建设道路的标志，它在新的历史条件下从经济方面（这是主要的）和政治方面提出了新的指导方针，为中共八大的召开作了理论准备。</p><h3 id="《关于正确处理人民内部矛盾的问题》" tabindex="-1"><a class="header-anchor" href="#《关于正确处理人民内部矛盾的问题》"><span>《关于正确处理人民内部矛盾的问题》</span></a></h3><h4 id="两类不同性质的社会矛盾" tabindex="-1"><a class="header-anchor" href="#两类不同性质的社会矛盾"><span>两类不同性质的社会矛盾</span></a></h4><p>在社会主义制度下，人民的根本利益是一致的，但还存在着敌我矛盾和人民内部矛盾。必须区分社会主义社会两类不同性质的社会矛盾，把正确处理人民内部矛盾作为国家政治生活的主题。</p><p>在政治思想领域，实行团结一批评一团结的方针；在物质利益、分配方面，实行统筹兼顾、适当安排的方针；对科学文化领域里的矛盾，实行“百花齐放，百家争鸣”的方针；对于共产党和民主党派的矛盾，实行在坚持社会主义道路和党的领导前提下的“长期共存，互相监督”的方针</p><h4 id="关于社会主义社会的基本矛盾" tabindex="-1"><a class="header-anchor" href="#关于社会主义社会的基本矛盾"><span>关于社会主义社会的基本矛盾</span></a></h4><p>社会主义社会充满着矛盾，正是这些矛盾推动着社会主义社会不断向前发展。在社会主义社会中，基本的矛盾仍然是生产关系和生产力之间的矛盾、上层建筑和经济基础之间的矛盾。社会主义社会基本矛盾是非对抗性的，可以通过社会主义制度本身的自我调整和自我完善不断得到解决。</p>',9),B={id:"探索中的严重曲折",tabindex:"-1"},T={class:"header-anchor",href:"#探索中的严重曲折"},V=e("p",null,"大跃进运动",-1),w=e("p",null,"文化大革命",-1);function C(S,z){const s=n("Badge");return o(),i("div",null,[e("h2",d,[e("a",r,[e("span",null,[t("全面建设社会主义的开端 "),l(s,{text:"选择题",type:"tip"})])])]),p,_,u,e("h2",m,[e("a",f,[e("span",null,[t("中共八大路线的制定 "),l(s,{text:"选择题",type:"tip"})])])]),x,g,b,v,e("h2",k,[e("a",y,[e("span",null,[t("探索社会主义建设道路的初步成果 "),l(s,{text:"选择题",type:"tip"})])])]),N,e("h2",B,[e("a",T,[e("span",null,[t("探索中的严重曲折 "),l(s,{text:"了解",type:"tip"})])])]),V,w])}const J=a(c,[["render",C],["__file","history9.html.vue"]]),O=JSON.parse('{"path":"/blogs/politics/history9.html","title":"9.社会主义建设在探索中曲折发展","lang":"zh-CN","frontmatter":{"title":"9.社会主义建设在探索中曲折发展","date":"2023-01-02T00:00:00.000Z"},"headers":[{"level":2,"title":"全面建设社会主义的开端","slug":"全面建设社会主义的开端","link":"#全面建设社会主义的开端","children":[]},{"level":2,"title":"中共八大路线的制定","slug":"中共八大路线的制定","link":"#中共八大路线的制定","children":[]},{"level":2,"title":"探索社会主义建设道路的初步成果","slug":"探索社会主义建设道路的初步成果","link":"#探索社会主义建设道路的初步成果","children":[{"level":3,"title":"《论十大关系》","slug":"《论十大关系》","link":"#《论十大关系》","children":[]},{"level":3,"title":"《关于正确处理人民内部矛盾的问题》","slug":"《关于正确处理人民内部矛盾的问题》","link":"#《关于正确处理人民内部矛盾的问题》","children":[]}]},{"level":2,"title":"探索中的严重曲折","slug":"探索中的严重曲折","link":"#探索中的严重曲折","children":[]}],"git":{"createdTime":1717234048000,"updatedTime":1718113098000,"contributors":[{"name":"passwordgloo","email":"passwordgloo@163.com","commits":1}]},"filePathRelative":"blogs/politics/history9.md"}');export{J as comp,O as data};
