import{_ as s,r as a,o as i,c as o,a as e,b as l,d as n,e as h}from"./app-CB_rd1hw.js";const d={},c={id:"社会主义核心价值观的基本内容",tabindex:"-1"},r={class:"header-anchor",href:"#社会主义核心价值观的基本内容"},_=h('<h3 id="核心价值观" tabindex="-1"><a class="header-anchor" href="#核心价值观"><span>核心价值观</span></a></h3><blockquote><p>核心价值观是一定社会形态社会性质的集中体现，在一个社会的思想观念体系中处于主导地位，体现着社会制度、社会运行的基本原则和社会发展的基本方向。</p></blockquote><h3 id="社会主义核心价值观" tabindex="-1"><a class="header-anchor" href="#社会主义核心价值观"><span>社会主义核心价值观</span></a></h3><p>社会主义核心价值观把涉及国家、社会、公民的价值要求融为一体，体现了社会主义本质要求，继承了中华优秀传统文化，吸收了世界文明有益成果，体现了时代精神，是对我们要建设什么样的国家、建设什么样的社会、培育什么样的公民等重大问题的深刻解答。</p><p>富强、民主、文明、和谐</p><p>自由、平等、公正、法治</p><p>爱国、敬业、诚信、友善</p><h3 id="社会主义核心价值观和社会主义核心价值体系的关系" tabindex="-1"><a class="header-anchor" href="#社会主义核心价值观和社会主义核心价值体系的关系"><span>社会主义核心价值观和社会主义核心价值体系的关系</span></a></h3><h4 id="社会主义核心价值体系的构成" tabindex="-1"><a class="header-anchor" href="#社会主义核心价值体系的构成"><span>社会主义核心价值体系的构成</span></a></h4><p>社会主义核心价值体系主要包括马克思主义指导思想、中国特色社会主义共同理想、以爱国主义为核心的民族精神和以改革创新为核心的时代精神、社会主义荣辱观。</p><h4 id="二者的关系" tabindex="-1"><a class="header-anchor" href="#二者的关系"><span>二者的关系</span></a></h4><p>一方面，<u>社会主义核心价值观是社会主义核心价值体系的精神内核</u>。它体现了社会主义核心价值体系的根本性质和基本特征，反映了社会主义核心价值体系的丰富内涵和实践要求，是社会主义核心价值体系的高度凝练和集中表达。</p><p>另一方面，<u>社会主义核心价值观与社会主义核心价值体系具有内在的一致性</u>。二者都体现了社会主义意识形态的本质要求，体现了社会主义制度在思想和精神层面的质的规定性，是建设中国特色社会主义现代化强国、实现中华民族伟大复兴中国梦的价值引领</p>',13),p={id:"当代中国发展进步的精神指引",tabindex:"-1"},u={class:"header-anchor",href:"#当代中国发展进步的精神指引"},x=e("p",null,"培育和践行社会主义核心价值观的重要性(必要性)：是有效整合我国社会意识、凝聚社会价值共识、解决和化解社会矛盾、聚合磅礴之力的重大举措，是保证我国经济社会沿着正确的方向发展、实现中华民族伟大复兴的价值支撑，意义重大而深远。 第一，坚持和发展中国特色社会主义的价值遵循。",-1),f=e("p",null,"第二，提高国家文化软实力的迫切要求",-1),b=e("p",null,"第三，增进社会团结和谐的最大公约数",-1),g={id:"社会主义核心价值观的历史底蕴",tabindex:"-1"},k={class:"header-anchor",href:"#社会主义核心价值观的历史底蕴"},v=e("p",null,"任何一种价值观都不可能凭空产生，总是有其特定的历史底色和精神脉络。社会主义核心价值观不是无源之水、无本之木。它深深地根植于中华优秀传统文化，是社会主义核心价值观历史底蕴的集中体现。 第一，中华优秀传统文化是涵养社会主义核心价值观的重要源泉，是中华民族的精神命脉。",-1),m=e("p",null,"第二，培育和弘扬社会主义核心价值观，必须立足中华优秀传统文化。",-1),y={id:"社会主义核心价值观的现实基础",tabindex:"-1"},N={class:"header-anchor",href:"#社会主义核心价值观的现实基础"},B=e("p",null,"社会主义核心价值观的现实基础：中国特色社会主义建设实践。 第一，中国特色社会主义建设是社会主义核心价值观的实践根据",-1),w=e("p",null,"第二，中国特色社会主义建设也以无可辩驳的事实生动展示着社会主义核心价值观的生机活力，中国改革开放以来探索出的中国特色社会主义道路却让中国发展成为人类发展史上的奇迹。中国特色社会主义建设的成功经验，是对社会主义核心价值观正确性、可信性的检验",-1),T={id:"社会主义核心价值观的道义力量",tabindex:"-1"},V={class:"header-anchor",href:"#社会主义核心价值观的道义力量"},q=e("blockquote",null,[e("p",null,"社会主义核心价值观以其先进性、人民性和真于人类社会的价值制高点，具有强大的道义力量。")],-1),C=e("p",null,"社会主义核心价值观的先进性，体现在它是社会主义制度所坚持和追求的核心价值理念。社会主义制度建立在生产资料公有制的基础之上，消灭了剥削制度，劳动人民成为国家的真正主人，是人类社会迄今为止最先进的社会制度。中国特色社会主义制度是科学社会主义原则与中国实际的创造性结合，至今仍在不断地改革、完善和发展之中。因此，作为人类社会最为先进社会制度的本质规定在价值层面的集中反映，社会主义核心价值观代表着当今时代人类社会的价值制高点。",-1),S=e("p",null,"社会主义核心价值观的人民性，体现在它所代表的最广大人民的根本利益，反映的最广大人民的价值诉求，引导着最广大人民为实现美好社会理想而奋斗。马克思主义最根本的政治立场，就是始终站在广大劳动人民的立场上，以广大劳动人民的解放为旨归，竭尽全力为人民求福利、谋利益。",-1),z={id:"做社会主义赅心价值观的积般践行者",tabindex:"-1"},E={class:"header-anchor",href:"#做社会主义赅心价值观的积般践行者"},J=e("h3",{id:"扣好人生的扣子",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#扣好人生的扣子"},[e("span",null,"扣好人生的扣子")])],-1),O=e("p",null,"青年的价值取向决定了未来整个社会的价值取向，而青年又处在价值观形成和确立的时期,抓好这一时期的价值观养成十分重要。正如习近平指岀的：“这就像穿衣服扣扣子一样，如果第一粒扣子扣错了，剩余的扣子都会扣错。人生的扣子从一开始就要扣好。”",-1),P=e("h3",{id:"勤学修德明辨笃实",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#勤学修德明辨笃实"},[e("span",null,"勤学修德明辨笃实")])],-1),R=e("p",null,"勤学：知识是树立社会主义核心价值观的重要基础",-1),Z=e("p",null,"修德：道德之于个人、之于社会，都具有基础性意义，做人做事第一位的是崇德修身",-1),j=e("p",null,"明辨：培育和践行社会主义核心价值观，要增强自己的价值判断力和道德责任感，辨别什么是真善美、什么是假恶丑，自觉做到常修善德、常怀善念、常做善举。",-1),A=e("p",null,"笃实：道不可坐论，德不能空谈。于实处用力，从知行合一上下功夫，核心价值观才能内化为人们的精神追求，外化为人们的自觉行动",-1);function D(F,G){const t=a("Badge");return i(),o("div",null,[e("h2",c,[e("a",r,[e("span",null,[l("社会主义核心价值观的基本内容 "),n(t,{text:"选择题",type:"tip"})])])]),_,e("h2",p,[e("a",u,[e("span",null,[l("当代中国发展进步的精神指引 "),n(t,{text:"选择题",type:"tip"})])])]),x,f,b,e("h2",g,[e("a",k,[e("span",null,[l("社会主义核心价值观的历史底蕴 "),n(t,{text:"选择题",type:"tip"})])])]),v,m,e("h2",y,[e("a",N,[e("span",null,[l("社会主义核心价值观的现实基础 "),n(t,{text:"选择题",type:"tip"})])])]),B,w,e("h2",T,[e("a",V,[e("span",null,[l("社会主义核心价值观的道义力量 "),n(t,{text:"选择题",type:"tip"})])])]),q,C,S,e("h2",z,[e("a",E,[e("span",null,[l("做社会主义赅心价值观的积般践行者 "),n(t,{text:"选择题",type:"tip"})])])]),J,O,P,R,Z,j,A])}const I=s(d,[["render",D],["__file","law5.html.vue"]]),K=JSON.parse('{"path":"/blogs/politics/law5.html","title":"5.社会主义核心价值观","lang":"zh-CN","frontmatter":{"title":"5.社会主义核心价值观","date":"2023-01-01T00:00:00.000Z"},"headers":[{"level":2,"title":"社会主义核心价值观的基本内容","slug":"社会主义核心价值观的基本内容","link":"#社会主义核心价值观的基本内容","children":[{"level":3,"title":"核心价值观","slug":"核心价值观","link":"#核心价值观","children":[]},{"level":3,"title":"社会主义核心价值观","slug":"社会主义核心价值观","link":"#社会主义核心价值观","children":[]},{"level":3,"title":"社会主义核心价值观和社会主义核心价值体系的关系","slug":"社会主义核心价值观和社会主义核心价值体系的关系","link":"#社会主义核心价值观和社会主义核心价值体系的关系","children":[]}]},{"level":2,"title":"当代中国发展进步的精神指引","slug":"当代中国发展进步的精神指引","link":"#当代中国发展进步的精神指引","children":[]},{"level":2,"title":"社会主义核心价值观的历史底蕴","slug":"社会主义核心价值观的历史底蕴","link":"#社会主义核心价值观的历史底蕴","children":[]},{"level":2,"title":"社会主义核心价值观的现实基础","slug":"社会主义核心价值观的现实基础","link":"#社会主义核心价值观的现实基础","children":[]},{"level":2,"title":"社会主义核心价值观的道义力量","slug":"社会主义核心价值观的道义力量","link":"#社会主义核心价值观的道义力量","children":[]},{"level":2,"title":"做社会主义赅心价值观的积般践行者","slug":"做社会主义赅心价值观的积般践行者","link":"#做社会主义赅心价值观的积般践行者","children":[{"level":3,"title":"扣好人生的扣子","slug":"扣好人生的扣子","link":"#扣好人生的扣子","children":[]},{"level":3,"title":"勤学修德明辨笃实","slug":"勤学修德明辨笃实","link":"#勤学修德明辨笃实","children":[]}]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"filePathRelative":"blogs/politics/law5.md"}');export{I as comp,K as data};
