import{_ as d,r,o as l,c as n,a,b as t,d as s,e}from"./app-CB_rd1hw.js";const p={},i=e('<h2 id="一-德育概述" tabindex="-1"><a class="header-anchor" href="#一-德育概述"><span>（一） 德育概述</span></a></h2><h3 id="_1-德育" tabindex="-1"><a class="header-anchor" href="#_1-德育"><span>1. 德育</span></a></h3><p>是教育者按照一定的社会或阶级的要求，有目的、有计划、系统地对受教育者积极的认识、体验与身体力行，以形成他们的品德和自我修养能力的教育活动。</p><h3 id="_2-德育任务" tabindex="-1"><a class="header-anchor" href="#_2-德育任务"><span>2. 德育任务</span></a></h3><p>德育要求学生追求善与明鉴是非，培养学生的道德信念和人生观，形成学生道德习惯</p><h3 id="_3-德育特性" tabindex="-1"><a class="header-anchor" href="#_3-德育特性"><span>3. 德育特性</span></a></h3><p>德育内容丰富性、德育方法多样化、</p><p>德育过程协同化、德育效果综合性、</p><p>德育途径多样性</p><h3 id="_4-德育的实施方式" tabindex="-1"><a class="header-anchor" href="#_4-德育的实施方式"><span>4. 德育的实施方式</span></a></h3>',10),c={href:"https://www.yuque.com/passwordgloo/principle/09#%E6%95%99%E8%82%B2%E5%BD%B1%E5%93%8D%E4%B8%80%E8%87%B4%E6%80%A7",target:"_blank",rel:"noopener noreferrer"},o=e('<h2 id="二-基本内容" tabindex="-1"><a class="header-anchor" href="#二-基本内容"><span>（二） 基本内容</span></a></h2><h3 id="_1-构成" tabindex="-1"><a class="header-anchor" href="#_1-构成"><span>1. 构成</span></a></h3><p>德育由政治教育，思想教育，法制教育和道德品质教育4个部分构成。</p><p>政治教育是方向，思想教育是基础，道德教育是核心，法制教育是保障</p><h3 id="_2-《小学德育纲要》" tabindex="-1"><a class="header-anchor" href="#_2-《小学德育纲要》"><span>2. 《小学德育纲要》</span></a></h3><p>对小学生进行社会公德教育和社会常识教育，培养训练学生良好道德品质和文明行为习惯</p><h3 id="_3-《中学德育大纲》" tabindex="-1"><a class="header-anchor" href="#_3-《中学德育大纲》"><span>3. 《中学德育大纲》</span></a></h3><p>培养学生热爱祖国，树立道德观念，法制观念，遵守社会公德，具有积极进取、自立自强的心理品质</p><h2 id="三-德育过程" tabindex="-1"><a class="header-anchor" href="#三-德育过程"><span>（三） 德育过程</span></a></h2><h3 id="_1-要素" tabindex="-1"><a class="header-anchor" href="#_1-要素"><span>1. 要素</span></a></h3><p>（1）教育者，起主导作用</p><p>（2）受教育者，德育主体与客体的统一</p><p>（3）德育内容，是教授受教育者学习修养的基本内容</p><h3 id="_2-德育过程的规律" tabindex="-1"><a class="header-anchor" href="#_2-德育过程的规律"><span>2. 德育过程的规律</span></a></h3><p>（1）德育过程是学生在教师指导下的个体品质的自主建构过程</p><p>（2）德育过程是培养学生知情意行整体和谐的发展过程</p><p>（3）德育过程是提高学生自我教育能力的过程</p><p>（4）德育过程是一个长期的，反复的，不断提高的过程</p><h2 id="四-德育原则" tabindex="-1"><a class="header-anchor" href="#四-德育原则"><span>（四） 德育原则</span></a></h2><h4 id="_1-集体教育与个别教育相统一" tabindex="-1"><a class="header-anchor" href="#_1-集体教育与个别教育相统一"><span>（1）集体教育与个别教育相统一</span></a></h4><table><thead><tr><th>内涵：</th><th>德育有赖于学生的社会交往，共同活动注意依靠学生集体通过集体活动进行教育，充分发挥学生集体在教育中的重要作用</th></tr></thead><tbody><tr><td>要求：</td><td>引导学生关心热爱集体，为建设良好的集体而努力通过集体教育学生个人，通过学生个人转变影响集体把教师的主导作用与集体教育力量结合起来</td></tr></tbody></table><h4 id="_2-理论和生活相结合原则-知行合一" tabindex="-1"><a class="header-anchor" href="#_2-理论和生活相结合原则-知行合一"><span>（2）理论和生活相结合原则(知行合一)</span></a></h4><table><thead><tr><th>内涵：</th><th>德育注意引导学生把思想政治观念和社会观念的学习同生活实践结合起来，做到心口如一，言行一致</th></tr></thead><tbody><tr><td>要求：</td><td>理论学习要结合学习生活实际，切实提高学生思想注重实践，培养道德行为习惯</td></tr></tbody></table><h4 id="_3-正面教育与纪律约束相结合原则" tabindex="-1"><a class="header-anchor" href="#_3-正面教育与纪律约束相结合原则"><span>（3）正面教育与纪律约束相结合原则</span></a></h4><table><thead><tr><th>内涵：</th><th>德育工作既要正面引导，启发自觉，又要辅之必要的纪律约束</th></tr></thead><tbody><tr><td>要求：</td><td>坚持正面教育原则坚持摆事实，讲道理，以理服人建立健全学校规章制度和集体组织公约并严格执行</td></tr></tbody></table><h4 id="_4-长善救失原则" tabindex="-1"><a class="header-anchor" href="#_4-长善救失原则"><span>（4）长善救失原则</span></a></h4><table><thead><tr><th>内涵：</th><th>德育依靠和发扬学生自身积极因素，去克服他们品德上的消极因素，促进学生道德成长</th></tr></thead><tbody><tr><td>要求：</td><td>一分为二看待学生，发扬积极因素，克服消极因素</td></tr></tbody></table><h4 id="_5-严格要求和尊重学生相结合" tabindex="-1"><a class="header-anchor" href="#_5-严格要求和尊重学生相结合"><span>（5）严格要求和尊重学生相结合</span></a></h4><table><thead><tr><th>内涵：</th><th>德育要把对学生思想品行的严格要求与对他们个人的尊重信赖结合起来，使严格要求转化为主动的道德自律</th></tr></thead><tbody><tr><td>要求：</td><td>尊重并信赖学生，严格要求学生</td></tr></tbody></table><h4 id="_6-教育影响一致性" tabindex="-1"><a class="header-anchor" href="#_6-教育影响一致性"><span>（6）教育影响一致性</span></a></h4><table><thead><tr><th>内涵：</th><th>德育应当有目的，有计划把来自各方面对学生影响加以组织，使其优化为教育的合力，前后连贯的进行，以获得最大的成效</th></tr></thead><tbody><tr><td>要求：</td><td>组建教师集体使校内对学生教育影响一致做好衔接工作，使对学生的教育前后连贯一致发挥学校教育的引领作用，使学校家庭和社会对学生的教育得到整合优化</td></tr></tbody></table><h2 id="五-德育方法" tabindex="-1"><a class="header-anchor" href="#五-德育方法"><span>（五） 德育方法</span></a></h2><h4 id="_1-明理教育法-说服教育" tabindex="-1"><a class="header-anchor" href="#_1-明理教育法-说服教育"><span>（1）明理教育法(说服教育)</span></a></h4><table><thead><tr><th>内涵：</th><th>引导学生把事实讲道理，经过思想情感上的沟通与互动，让学生悟明道德真谛，自觉践行的方法</th></tr></thead><tbody><tr><td>要求：</td><td>教师一方面讲明道理，另一方面加强同学生情感沟通，情感认同</td></tr></tbody></table><h4 id="_2-情境陶冶法-情感陶冶" tabindex="-1"><a class="header-anchor" href="#_2-情境陶冶法-情感陶冶"><span>（2）情境陶冶法(情感陶冶)</span></a></h4><table><thead><tr><th>内涵：</th><th>通过创设良好的教育情境，潜移默化地培养学生品德的方法，利用暗示原理，让学生通过无意识的心理活动来接受某种影响</th></tr></thead><tbody><tr><td>要求：</td><td>创设良好情境，与启发引导相结合，引导学生参与</td></tr></tbody></table><h4 id="_3-实践锻炼法-实践锻炼" tabindex="-1"><a class="header-anchor" href="#_3-实践锻炼法-实践锻炼"><span>（3）实践锻炼法(实践锻炼)</span></a></h4><table><thead><tr><th>内涵：</th><th>是有目的有组织的安排学生进行一定的生活交往与社会践行活动，以培养道德的方法</th></tr></thead><tbody><tr><td>要求：</td><td>注意调动学生的积极性，教师给予适当的指导，严格要求学生，及时检查并长期坚持</td></tr></tbody></table><h4 id="_4-自我修养法-自我教育" tabindex="-1"><a class="header-anchor" href="#_4-自我修养法-自我教育"><span>（4）自我修养法(自我教育)</span></a></h4><table><thead><tr><th>内涵：</th><th>在教师引导下，学生经过自学学习反思和自我改进，使自身品德不断完善的一种方法，包括立志、学习、反思、箴言、慎独</th></tr></thead><tbody><tr><td>要求：</td><td>培养学生自我修养的兴趣与自觉性，指导学生掌握修养的标准，引导学生积极参加社会实践</td></tr></tbody></table><h4 id="_5-榜样示范法-榜样示范" tabindex="-1"><a class="header-anchor" href="#_5-榜样示范法-榜样示范"><span>（5）榜样示范法(榜样示范)</span></a></h4><table><thead><tr><th>内涵：</th><th>是以他人的高尚品德模范行为和卓越成就来影响学生品德的方法</th></tr></thead><tbody><tr><td>要求：</td><td>选用真实核心的榜样，激起学生积极引导，注意教师自身示范作用，给不同年龄段的学生树立不同的榜样</td></tr></tbody></table><h4 id="_6-奖惩法" tabindex="-1"><a class="header-anchor" href="#_6-奖惩法"><span>（6）奖惩法</span></a></h4><table><thead><tr><th>内涵：</th><th>奖惩是对学生思想和行为作出评价，包括表扬奖励和批评</th></tr></thead><tbody><tr><td>要求：</td><td>公平公正，发扬民主，注重宣传教育</td></tr></tbody></table><h4 id="_7-制度育德法" tabindex="-1"><a class="header-anchor" href="#_7-制度育德法"><span>（7）制度育德法</span></a></h4><table><thead><tr><th>内涵：</th><th>通过构建合理的学校制度来引导和培养学生品德的方法</th></tr></thead><tbody><tr><td>要求：</td><td>学校制度要合法，学校制度合乎道德要求</td></tr></tbody></table><h2 id="六-德育模式" tabindex="-1"><a class="header-anchor" href="#六-德育模式"><span>（六） 德育模式</span></a></h2><h3 id="_1-道德认知发展模式" tabindex="-1"><a class="header-anchor" href="#_1-道德认知发展模式"><span>1. 道德认知发展模式</span></a></h3><h4 id="_1-皮亚杰道德发展理论" tabindex="-1"><a class="header-anchor" href="#_1-皮亚杰道德发展理论"><span>（1）皮亚杰道德发展理论</span></a></h4><p>皮亚杰通过“对偶故事”观察实验，揭示儿童道德判断的发展进程，将儿童道德分发展分为4个阶段：</p><table><thead><tr><th>阶段</th><th>内容</th></tr></thead><tbody><tr><td>自我中心阶段(2-5)</td><td>儿童以“自我中心”考虑问题，将环境看作自身延伸</td></tr><tr><td>权威(他律)阶段(5-8)</td><td>服从外部规则，接受权威指定规范，将规则看成是一成不变</td></tr><tr><td>可逆性阶段(8-10)</td><td>规则是同伴间共同约定的，同伴可逆关系，由他律转向自律</td></tr><tr><td>公正阶段(10-12)</td><td>倾向于主持公平公正</td></tr></tbody></table><h4 id="_2-布莱特效应" tabindex="-1"><a class="header-anchor" href="#_2-布莱特效应"><span>（2）布莱特效应</span></a></h4><p>儿童倾向于拒斥低于自己道德发展阶段的同伴的道德推理，并且能够理解和同化高于自己阶段同伴的道德推理，但难以理解和接受高于自己两个或两个以上阶段的同伴的道德推理。</p><h4 id="_3-科尔伯格的道德发展理论" tabindex="-1"><a class="header-anchor" href="#_3-科尔伯格的道德发展理论"><span>（3）科尔伯格的道德发展理论</span></a></h4><p>柯尔伯格编制“道德两难故事”作为儿童道德判断的工具，采取公正团体法<a href="%E5%88%A9%E7%94%A8%E5%85%AC%E6%AD%A3%E7%9A%84%E6%9C%BA%E5%88%B6%E5%9C%A8%E5%88%9B%E8%AE%BE%E5%85%AC%E6%AD%A3%E5%9B%A2%E4%BD%93%E4%B8%AD%E5%9F%B9%E5%85%BB%E5%AD%A6%E7%94%9F%E7%9A%84%E5%85%AC%E6%AD%A3%E8%A7%82%E5%BF%B5%EF%BC%8C%E8%BE%BE%E5%88%B0%E6%9B%B4%E9%AB%98%E7%9A%84%E9%81%93%E5%BE%B7%E5%8F%91%E5%B1%95%E6%B0%B4%E5%B9%B3%E3%80%82%E5%AE%83%E5%BC%BA%E8%B0%83%E7%9A%84%E6%98%AF%E5%9B%A2%E4%BD%93%E7%9A%84%E6%95%99%E8%82%B2%E5%8A%9B%E9%87%8F%E5%92%8C%E6%B0%91%E4%B8%BB%E6%9C%BA%E6%9E%84%E7%9A%84%E6%95%99%E8%82%B2%E4%BD%9C%E7%94%A8%E3%80%82">^1</a>对儿童的道德判断能力进行研究，形成三水平六阶段道德发展阶段论</p><p>①前习俗水平</p><p>惩罚与服从定向阶段，服从权威只是为避免惩罚，以行为后果判断行为是好是坏的程度</p><p>相对功利取向阶段，道德价值来自对己需要的满足，他们不再把规则看成是绝对的，固定不变的，评定行为的好坏看是否符合自己利益</p><p>②习俗水平</p><p>寻求认可定向阶段，谋求大家赞赏，注意和谐人际关系</p><p>遵守法规和秩序定向阶段，服从权威，遵守秩序，知法懂法</p><p>③后习俗水平</p><p>社会契约定向阶段，看重法律效力，可以改变法律</p><p>原则或良心定向阶段，公平公正，尊严，思考，判断</p><h3 id="_2-体谅模式" tabindex="-1"><a class="header-anchor" href="#_2-体谅模式"><span>2. 体谅模式</span></a></h3><p>英国教育家麦克·弗尔等人编制的《生命线》由三部分组成</p><p>第一部分《设身处地》，含《敏感性》《后果》《观点》</p><p>第二部分《证明规则》，含《规则与个体》《你期望什么？》《你认为我是谁？》</p><p>第三部分《你会怎么办》，含《生日》《幽闭》《悲剧》在德育中最重要的是关心和体谅别人，把道德情感的培养放在中心地位。即儿童能从别人的观点和立场出发来考察自己的道德行为，鼓励处于社会试验期的青少年试验各种不同的角色和身份。置身事外，从旁观者的立场预估人际行为后果。</p><h3 id="_3-价值澄清模式" tabindex="-1"><a class="header-anchor" href="#_3-价值澄清模式"><span>3. 价值澄清模式</span></a></h3><table><thead><tr><th>基本原理：</th><th>核心理论是学会选择，澄清自己的价值就是在不同的价值中进行自由选择</th></tr></thead><tbody><tr><td>理论主张：</td><td>社会是变化发展的，不仅要传授某种固定的价值观点，还要教会学生如何分析不同的道德价值，善于在复杂的社会情境中做出明智的抉择。</td></tr></tbody></table><h3 id="_4-社会学习模式" tabindex="-1"><a class="header-anchor" href="#_4-社会学习模式"><span>4. 社会学习模式</span></a></h3><ul><li><p>学习是指个体通过对他人的行为及其强化性结果的观察从而获得某些新的行为反应，或已有的行为反应得到修正的过程</p></li><li><p>决定行为的先行因素和决定行为的结果因素是相互作用的</p></li><li><p>自我调节是个人的内在强化过程</p></li><li><p>自我效能是指个体对自己能否在一定水平上完成某一活动所具有的能力判断，信念或主体自我把握与感受</p></li></ul><h3 id="_5-集体教育模式" tabindex="-1"><a class="header-anchor" href="#_5-集体教育模式"><span>5. 集体教育模式</span></a></h3><p>前苏联教育家马卡连轲创立集体教育模式，主要内容包括：</p><p>（1）教育工作的主要方式是集体教育</p><p>（2）在集体中，通过集体，为了集体的教育体系</p><p>（3）平行教育影响原则和前景教育原则</p><p>（4）尊重，信任与严格要求相结合原则</p><hr>',80);function _(b,E){const h=r("ExternalLinkIcon");return l(),n("div",null,[i,a("p",null,[t("直接的德育途径：直接的德育课程；间接的德育途径：其他学科课程中的德育任务，主张德育渗透于一切课程和教学之中（参见"),a("a",c,[t("教育影响一致性"),s(h)]),t("）。")]),o])}const f=d(p,[["render",_],["__file","principle9.html.vue"]]),x=JSON.parse('{"path":"/blogs/education/principle9.html","title":"德育","lang":"zh-CN","frontmatter":{"title":"德育"},"headers":[{"level":2,"title":"（一） 德育概述","slug":"一-德育概述","link":"#一-德育概述","children":[{"level":3,"title":"1. 德育","slug":"_1-德育","link":"#_1-德育","children":[]},{"level":3,"title":"2. 德育任务","slug":"_2-德育任务","link":"#_2-德育任务","children":[]},{"level":3,"title":"3. 德育特性","slug":"_3-德育特性","link":"#_3-德育特性","children":[]},{"level":3,"title":"4. 德育的实施方式","slug":"_4-德育的实施方式","link":"#_4-德育的实施方式","children":[]}]},{"level":2,"title":"（二） 基本内容","slug":"二-基本内容","link":"#二-基本内容","children":[{"level":3,"title":"1. 构成","slug":"_1-构成","link":"#_1-构成","children":[]},{"level":3,"title":"2. 《小学德育纲要》","slug":"_2-《小学德育纲要》","link":"#_2-《小学德育纲要》","children":[]},{"level":3,"title":"3. 《中学德育大纲》","slug":"_3-《中学德育大纲》","link":"#_3-《中学德育大纲》","children":[]}]},{"level":2,"title":"（三） 德育过程","slug":"三-德育过程","link":"#三-德育过程","children":[{"level":3,"title":"1. 要素","slug":"_1-要素","link":"#_1-要素","children":[]},{"level":3,"title":"2. 德育过程的规律","slug":"_2-德育过程的规律","link":"#_2-德育过程的规律","children":[]}]},{"level":2,"title":"（四） 德育原则","slug":"四-德育原则","link":"#四-德育原则","children":[]},{"level":2,"title":"（五） 德育方法","slug":"五-德育方法","link":"#五-德育方法","children":[]},{"level":2,"title":"（六） 德育模式","slug":"六-德育模式","link":"#六-德育模式","children":[{"level":3,"title":"1. 道德认知发展模式","slug":"_1-道德认知发展模式","link":"#_1-道德认知发展模式","children":[]},{"level":3,"title":"2. 体谅模式","slug":"_2-体谅模式","link":"#_2-体谅模式","children":[]},{"level":3,"title":"3. 价值澄清模式","slug":"_3-价值澄清模式","link":"#_3-价值澄清模式","children":[]},{"level":3,"title":"4. 社会学习模式","slug":"_4-社会学习模式","link":"#_4-社会学习模式","children":[]},{"level":3,"title":"5. 集体教育模式","slug":"_5-集体教育模式","link":"#_5-集体教育模式","children":[]}]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"filePathRelative":"blogs/education/principle9.md"}');export{f as comp,x as data};
